// FUN√á√ÉO: wa_update_contact (do GAS)
const out = [];
for (const { json } of items) {
  const { site, siteSlug, id, telefone, nome, email, empresa, tags, ativo, profilePicUrl } = json.body || {};
  const normalizedSite = (site || siteSlug || '').toUpperCase();

  if (!normalizedSite) {
    out.push({
      json: {
        success: false,
        error: 'Site √© obrigat√≥rio',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  if (!id && !telefone) {
    out.push({
      json: {
        success: false,
        error: 'ID ou telefone √© obrigat√≥rio',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  // Normalizar telefone se fornecido
  let normalizedPhone = null;
  if (telefone) {
    const digits = telefone.replace(/\D/g, '');
    normalizedPhone = digits.length === 11 && !digits.startsWith('55') ? '55' + digits : digits;
  }

  const searchFormula = id 
    ? `AND({id}='${id}', {siteSlug}='${normalizedSite}')`
    : `AND({telefone_normalizado}='${normalizedPhone}', {siteSlug}='${normalizedSite}')`;

  out.push({
    json: {
      siteSlug: normalizedSite,
      searchFormula: searchFormula,
      updates: {
        nome: nome || undefined,
        email: email || undefined,
        empresa: empresa || undefined,
        tags: tags || undefined,
        ativo: typeof ativo === 'boolean' ? ativo : undefined,
        profilePicUrl: profilePicUrl || undefined,
        atualizado_em: new Date().toISOString()
      },
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// FUN√á√ÉO: wa_upsert_template (do GAS)
const crypto = require('crypto');
const out = [];

for (const { json } of items) {
  const { site, siteSlug, template } = json.body || {};
  const normalizedSite = (site || siteSlug || '').toUpperCase();

  if (!normalizedSite || !template) {
    out.push({
      json: {
        success: false,
        error: 'Site e template s√£o obrigat√≥rios',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  const templateData = {
    id: template.id || crypto.randomUUID(),
    siteSlug: normalizedSite,
    name: template.name || '',
    displayName: template.displayName || '',
    lang: template.lang || 'pt_BR',
    description: template.description || '',
    category: template.category || '',
    status: template.status || 'APPROVED',
    content: template.content || '',
    variables: JSON.stringify(template.variables || []),
    createdAt: template.createdAt || new Date().toISOString(),
    updatedAt: new Date().toISOString()
  };

  // Se tem ID, √© update; sen√£o √© insert
  const isUpdate = !!template.id;
  const searchFormula = isUpdate ? `AND({id}='${templateData.id}', {siteSlug}='${normalizedSite}')` : null;

  out.push({
    json: {
      ...templateData,
      isUpdate: isUpdate,
      searchFormula: searchFormula,
      timestamp: new Date().toISOString()
    }
  });
}
return out;
