// Paginar e formatar contatos (baseado no GAS linha 1103)
const out = [];
const inputNode = $node['Code - Prepare Contacts Query'].json;
const page = inputNode.page || 1;
const pageSize = inputNode.pageSize || 20;
const filters = inputNode.filters || {};

let contacts = items.map(i => i.json.fields || i.json);

// Aplicar filtros adicionais
if (filters.nome) {
  contacts = contacts.filter(c => (c.nome || '').toLowerCase().includes(filters.nome.toLowerCase()));
}
if (filters.empresa) {
  contacts = contacts.filter(c => (c.empresa || '').toLowerCase().includes(filters.empresa.toLowerCase()));
}
if (filters.tags) {
  contacts = contacts.filter(c => (c.tags || '').includes(filters.tags));
}

// Ordenar por data de cria√ß√£o (mais recente primeiro)
contacts.sort((a, b) => new Date(b.criado_em || 0) - new Date(a.criado_em || 0));

// Paginar
const total = contacts.length;
const start = (page - 1) * pageSize;
const paginatedContacts = contacts.slice(start, start + pageSize);

out.push({
  json: {
    success: true,
    data: {
      contacts: paginatedContacts,
      total: total,
      page: page,
      pageSize: pageSize,
      hasMore: start + pageSize < total
    },
    timestamp: new Date().toISOString()
  }
});
return out;

// FUN√á√ÉO: wa_import_contacts (do GAS linha 1192)
const crypto = require('crypto');
const out = [];

for (const { json } of items) {
  const { site, siteSlug, contacts = [] } = json.body || {};
  const normalizedSite = (site || siteSlug || '').toUpperCase();

  if (!normalizedSite) {
    out.push({
      json: {
        success: false,
        error: 'Site √© obrigat√≥rio',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  if (!Array.isArray(contacts) || contacts.length === 0) {
    out.push({
      json: {
        success: false,
        error: 'Lista de contatos √© obrigat√≥ria e deve ter ao menos 1 item',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  // Validar e normalizar cada contato
  const validContacts = [];
  const errors = [];

  contacts.forEach((contact, index) => {
    const phone = (contact.phone || contact.telefone || '').replace(/\D/g, '');
    const nome = (contact.nome || contact.name || '').trim();

    if (!phone) {
      errors.push({ index, error: 'Telefone obrigat√≥rio' });
      return;
    }

    if (!nome) {
      errors.push({ index, error: 'Nome obrigat√≥rio' });
      return;
    }

    // Normalizar telefone para E.164 BR
    let normalized = phone;
    if (phone.length === 11 && !phone.startsWith('55')) {
      normalized = '55' + phone;
    } else if (phone.length === 10) {
      normalized = '55' + phone.substring(0, 2) + '9' + phone.substring(2);
    } else if (phone.length === 9) {
      normalized = '5596' + phone;
    } else if (phone.length === 8) {
      normalized = '55969' + phone;
    }

    validContacts.push({
      id: crypto.randomUUID(),
      siteSlug: normalizedSite,
      nome: nome,
      telefone_normalizado: normalized,
      empresa: contact.empresa || '',
      email: contact.email || '',
      tags: contact.tags || '',
      profilePicUrl: contact.profilePicUrl || '',
      ativo: true,
      criado_em: new Date().toISOString(),
      atualizado_em: new Date().toISOString()
    });
  });

  // Emitir cada contato v√°lido separadamente para o Airtable
  validContacts.forEach(contact => {
    out.push({ json: contact });
  });

  // Adicionar um √∫ltimo item com o resumo
  if (validContacts.length > 0) {
    out.push({
      json: {
        __summary: true,
        success: true,
        siteSlug: normalizedSite,
        total: contacts.length,
        valid: validContacts.length,
        errors: errors.length,
        errorDetails: errors,
        timestamp: new Date().toISOString()
      }
    });
  }
}
return out;

