// FUNÃ‡ÃƒO: audit_get_security_alerts (do GAS linha 1427)
const out = [];
for (const { json } of items) {
  const { site, siteSlug, severity, resolved = false } = json.body || {};
  const normalizedSite = (site || siteSlug || '').toUpperCase();

  let searchFormula = normalizedSite ? `{site}='${normalizedSite}'` : '1=1';

  if (severity) {
    searchFormula = `AND(${searchFormula}, {severity}='${severity}')`;  
  }

  if (typeof resolved === 'boolean') {
    searchFormula = `AND(${searchFormula}, {resolved}=${resolved ? 'TRUE()' : 'FALSE()'})`;
  }

  out.push({
    json: {
      siteSlug: normalizedSite || 'ALL',
      searchFormula: searchFormula,
      needsAdminCheck: true,
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// Formatar e ordenar alertas por severity
const out = [];
const alerts = items.map(i => i.json.fields || i.json);

// Ordem de severity
const severityOrder = { critical: 4, high: 3, medium: 2, low: 1 };

alerts.sort((a, b) => {
  const severityA = severityOrder[a.severity] || 0;
  const severityB = severityOrder[b.severity] || 0;
  if (severityA !== severityB) return severityB - severityA;
  return new Date(b.createdAt) - new Date(a.createdAt);
});

const criticalCount = alerts.filter(a => a.severity === 'critical').length;
const highCount = alerts.filter(a => a.severity === 'high').length;
const unresolvedCount = alerts.filter(a => !a.resolved).length;

out.push({
  json: {
    success: true,
    data: {
      alerts: alerts,
      total: alerts.length,
      criticalCount: criticalCount,
      highCount: highCount,
      unresolvedCount: unresolvedCount
    },
    timestamp: new Date().toISOString()
  }
});
return out;

// FUNÃ‡ÃƒO: audit_resolve_alert (do GAS linha 1429)
const out = [];
for (const { json } of items) {
  const { alertId, resolvedBy, notes } = json.body || {};

  if (!alertId) {
    out.push({
      json: {
        success: false,
        error: 'Alert ID Ã© obrigatÃ³rio',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  out.push({
    json: {
      alertId: alertId,
      searchFormula: `{id}='${alertId}'`,
      resolvedBy: resolvedBy || 'admin',
      notes: notes || '',
      needsAdminCheck: true,
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// FUNÃ‡ÃƒO: audit_get_statistics (do GAS linha 1430)
const out = [];
for (const { json } of items) {
  const { site, siteSlug, range = '30d' } = json.body || {};
  const normalizedSite = (site || siteSlug || '').toUpperCase();

  // Calcular cutoff date
  const days = range === '7d' ? 7 : range === '30d' ? 30 : 90;
  const cutoffDate = new Date();
  cutoffDate.setDate(cutoffDate.getDate() - days);

  let searchFormula = `{timestamp}>='${cutoffDate.toISOString()}'`;
  if (normalizedSite) {
    searchFormula = `AND({site}='${normalizedSite}', ${searchFormula})`;
  }

  out.push({
    json: {
      siteSlug: normalizedSite || 'ALL',
      range: range,
      cutoffDate: cutoffDate.toISOString(),
      searchFormula: searchFormula,
      needsAdminCheck: true,
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// Agregar estatÃ­sticas de audit (baseado no GAS)
const logs = items.map(i => i.json.fields || i.json);

// Total eventos
const total = logs.length;

// Por categoria
const byCategory = {};
logs.forEach(log => {
  const cat = log.category || 'other';
  byCategory[cat] = (byCategory[cat] || 0) + 1;
});

// Por severity
const bySeverity = {};
logs.forEach(log => {
  const sev = log.severity || 'info';
  bySeverity[sev] = (bySeverity[sev] || 0) + 1;
});

// Por usuÃ¡rio
const byUser = {};
logs.forEach(log => {
  const user = log.userId || 'system';
  byUser[user] = (byUser[user] || 0) + 1;
});

// Por site
const bySite = {};
logs.forEach(log => {
  const site = log.site || 'unknown';
  bySite[site] = (bySite[site] || 0) + 1;
});

// Top aÃ§Ãµes
const actionCounts = {};
logs.forEach(log => {
  const action = log.action || 'unknown';
  actionCounts[action] = (actionCounts[action] || 0) + 1;
});
const topActions = Object.entries(actionCounts)
  .map(([action, count]) => ({ action, count }))
  .sort((a, b) => b.count - a.count)
  .slice(0, 10);

// Timeline diÃ¡ria
const dailyStats = {};
logs.forEach(log => {
  const date = new Date(log.timestamp).toISOString().split('T')[0];
  if (!dailyStats[date]) dailyStats[date] = 0;
  dailyStats[date]++;
});

const dailyArray = Object.entries(dailyStats)
  .map(([date, count]) => ({ date, count }))
  .sort((a, b) => new Date(a.date) - new Date(b.date));

const out = [{
  json: {
    success: true,
    data: {
      total: total,
      byCategory: byCategory,
      bySeverity: bySeverity,
      byUser: byUser,
      bySite: bySite,
      topActions: topActions,
      dailyStats: dailyArray
    },
    timestamp: new Date().toISOString()
  }
}];
return out;
