// FUNÃ‡ÃƒO: get_analytics / get_traffic (do GAS)
const out = [];
for (const { json } of items) {
  const query = json.query || {};
  const { siteSlug, startDate, endDate } = query;

  out.push({
    json: {
      siteSlug: siteSlug || 'all',
      startDate: startDate || new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString(),
      endDate: endDate || new Date().toISOString(),
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// Processar e agregar analytics
const out = [];
const events = items.map(i => i.json.fields || {});

// Contar por tipo de evento
const eventCounts = {};
events.forEach(e => {
  const type = e.event_type || 'unknown';
  eventCounts[type] = (eventCounts[type] || 0) + 1;
});

// Contar por dispositivo
const deviceCounts = {};
events.forEach(e => {
  const device = e.device_type || 'unknown';
  deviceCounts[device] = (deviceCounts[device] || 0) + 1;
});

// Contar por navegador
const browserCounts = {};
events.forEach(e => {
  const browser = e.browser || 'unknown';
  browserCounts[browser] = (browserCounts[browser] || 0) + 1;
});

out.push({
  json: {
    success: true,
    analytics: {
      totalEvents: events.length,
      eventsByType: eventCounts,
      deviceBreakdown: deviceCounts,
      browserBreakdown: browserCounts,
      topEvents: Object.entries(eventCounts).sort((a, b) => b[1] - a[1]).slice(0, 5)
    },
    timestamp: new Date().toISOString()
  }
});
return out;
