// FUN√á√ÉO: appointment_get_availability (do GAS linha 6989)
const out = [];
for (const { json } of items) {
  const { site, siteSlug, date, duration = 60 } = json.body || {};
  const normalizedSite = (site || siteSlug || '').toUpperCase();

  if (!normalizedSite || !date) {
    out.push({
      json: {
        success: false,
        error: 'Site e data s√£o obrigat√≥rios',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  out.push({
    json: {
      siteSlug: normalizedSite,
      date: date,
      duration: parseInt(duration),
      searchFormula: `AND({site}='${normalizedSite}', {datetime}>='${date}T00:00:00', {datetime}<='${date}T23:59:59', {status}<>'cancelled')`,
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// Calcular slots dispon√≠veis (baseado no GAS linha 6989 + helper checkAppointmentAvailability_ linha 223)
const appointments = items[0]?.json?.appointments || [];
const settings = items[1]?.json?.fields || {};
const inputData = $node['Code - Prepare Availability Check'].json;

// Working hours
let workingHours = { start: '09:00', end: '18:00' };
try {
  workingHours = typeof settings.workingHours === 'string' 
    ? JSON.parse(settings.workingHours) 
    : (settings.workingHours || workingHours);
} catch (e) {}

const duration = inputData.duration || 60;
const startHour = parseInt(workingHours.start.split(':')[0]);
const endHour = parseInt(workingHours.end.split(':')[0]);

const availableSlots = [];
const busySlots = [];

for (let hour = startHour; hour < endHour; hour++) {
  const timeSlot = `${hour.toString().padStart(2, '0')}:00`;
  const slotDateTime = `${inputData.date}T${timeSlot}:00`;

  // Verificar se h√° conflito (checkAppointmentAvailability logic)
  const requestedStart = new Date(slotDateTime);
  const requestedEnd = new Date(requestedStart.getTime() + (duration * 60000));

  let hasConflict = false;
  for (const apt of appointments) {
    if (apt.status === 'cancelled') continue;

    const existingStart = new Date(apt.datetime);
    const existingEnd = new Date(existingStart.getTime() + ((apt.duration || 60) * 60000));

    if (requestedStart < existingEnd && requestedEnd > existingStart) {
      hasConflict = true;
      busySlots.push(timeSlot);
      break;
    }
  }

  if (!hasConflict) {
    availableSlots.push(timeSlot);
  }
}

const out = [{
  json: {
    success: true,
    data: {
      date: inputData.date,
      availableSlots: availableSlots,
      busySlots: busySlots,
      workingHours: workingHours,
      duration: duration
    },
    timestamp: new Date().toISOString()
  }
}];
return out;
