// FUN√á√ÉO: client_plan / get_client_plan_v2 (do GAS)
const out = [];
for (const { json } of items) {
  const { siteSlug, email } = json.body || {};

  if (!siteSlug && !email) {
    out.push({
      json: {
        success: false,
        error: 'siteSlug ou email √© obrigat√≥rio',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  out.push({
    json: {
      success: true,
      siteSlug: siteSlug,
      email: email,
      needsClientData: true,
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// Processar dados completos do cliente com billing
const out = [];
for (const { json } of items) {
  const client = json.fields || {};
  const plan = client.plan || 'essential';
  const isVip = plan === 'vip';

  const coreFeatures = ['basic-website', 'google-my-business'];
  const vipFeatures = [
    'ai-copywriter', 'auto-seo', 'lead-scoring', 'whatsapp-chatbot',
    'appointment-scheduling', 'multi-language', 'ecommerce',
    'premium-templates', 'white-label', 'audit-logs', 'feedback-system'
  ];

  out.push({
    json: {
      success: true,
      data: {
        siteSlug: client.siteSlug,
        email: client.email,
        name: client.name,
        plan: plan,
        status: client.status || 'active',
        features: isVip ? [...coreFeatures, ...vipFeatures] : coreFeatures,
        isVip: isVip,
        billing: {
          provider: 'mercadopago',
          preapproval_id: client.preapproval_id || '',
          last_payment: client.last_payment || null,
          amount: isVip ? 97 : 0,
          payment_method: client.payment_method || null
        },
        limits: {
          websites: isVip ? -1 : 1,
          whatsapp_messages: isVip ? -1 : 100,
          ai_requests: isVip ? -1 : 10,
          storage: isVip ? 'unlimited' : '1GB'
        },
        createdAt: client.created_at,
        updatedAt: client.updated_at || new Date().toISOString()
      },
      timestamp: new Date().toISOString()
    }
  });
}
return out;
