const out = [];
for (const { json } of items) {
  const { customerEmail } = json.body || {};

  if (!customerEmail) {
    out.push({
      json: {
        success: false,
        error: 'Customer email Ã© obrigatÃ³rio',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  out.push({
    json: {
      customerEmail: customerEmail.toLowerCase().trim(),
      searchFormula: `{customerEmail}='${customerEmail.toLowerCase().trim()}'`,
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// Formatar lista de compras
const out = [];
const purchases = items.map(i => {
  const fields = i.json.fields || i.json;
  return {
    purchaseId: fields.id,
    templateId: fields.templateId,
    templateName: fields.templateName || 'Template',
    licenseKey: fields.licenseKey,
    downloadUrl: fields.downloadUrl,
    purchaseDate: fields.purchaseDate,
    pricePaid: fields.pricePaid,
    currency: fields.currency,
    status: fields.status
  };
});

// Ordenar por data (mais recente primeiro)
purchases.sort((a, b) => new Date(b.purchaseDate) - new Date(a.purchaseDate));

out.push({
  json: {
    success: true,
    data: {
      purchases: purchases,
      total: purchases.length
    },
    timestamp: new Date().toISOString()
  }
});
return out;
