// FUN√á√ÉO: list_modules / get_active_modules (do GAS)
const out = [];
for (const { json } of items) {
  const query = json.query || {};
  const { siteSlug } = query;

  if (!siteSlug) {
    out.push({
      json: {
        success: false,
        error: 'siteSlug √© obrigat√≥rio',
        timestamp: new Date().toISOString()
      }
    });
