// Retornar m√≥dulos dispon√≠veis baseado no plano
const out = [];
for (const { json } of items) {
  const client = json.fields || {};
  const plan = client.plan || 'essential';
  const isVip = plan === 'vip';

  const allModules = [
    { id: 'basic-website', name: 'Site B√°sico', enabled: true, requiredPlan: 'essential' },
    { id: 'google-my-business', name: 'Google Meu Neg√≥cio', enabled: true, requiredPlan: 'essential' },
    { id: 'ai-copywriter', name: 'IA Copywriter', enabled: isVip, requiredPlan: 'vip' },
    { id: 'auto-seo', name: 'SEO Autom√°tico', enabled: isVip, requiredPlan: 'vip' },
    { id: 'lead-scoring', name: 'Lead Scoring', enabled: isVip, requiredPlan: 'vip' },
    { id: 'whatsapp-chatbot', name: 'Chatbot WhatsApp', enabled: isVip, requiredPlan: 'vip' },
    { id: 'appointment-scheduling', name: 'Agendamentos', enabled: isVip, requiredPlan: 'vip' },
    { id: 'multi-language', name: 'Multi-idioma', enabled: isVip, requiredPlan: 'vip' },
    { id: 'ecommerce', name: 'E-commerce', enabled: isVip, requiredPlan: 'vip' },
    { id: 'premium-templates', name: 'Templates Premium', enabled: isVip, requiredPlan: 'vip' },
    { id: 'white-label', name: 'White Label', enabled: isVip, requiredPlan: 'vip' },
    { id: 'audit-logs', name: 'Logs de Auditoria', enabled: isVip, requiredPlan: 'vip' },
    { id: 'feedback-system', name: 'Sistema de Feedback', enabled: isVip, requiredPlan: 'vip' }
  ];

  out.push({
    json: {
      success: true,
      siteSlug: client.siteSlug,
      plan: plan,
      modules: allModules,
      enabledCount: allModules.filter(m => m.enabled).length,
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// FUN√á√ÉO: toggle_module (do GAS)
const out = [];
for (const { json } of items) {
  const { siteSlug, moduleId, enabled } = json.body || {};

  if (!siteSlug || !moduleId) {
    out.push({
      json: {
        success: false,
        error: 'siteSlug e moduleId s√£o obrigat√≥rios',
        timestamp: new Date().toISOString()
      }
    });
    continue;
  }

  out.push({
    json: {
      siteSlug,
      moduleId,
      enabled: enabled !== false,
      timestamp: new Date().toISOString()
    }
  });
}
return out;

// Validar se cliente pode ativar m√≥dulo (VIP)
const out = [];
for (const { json } of items) {
  const client = json.fields || {};
  const plan = client.plan || 'essential';
  const isVip = plan === 'vip';

  const vipModules = ['ai-copywriter', 'auto-seo', 'lead-scoring', 'whatsapp-chatbot', 'appointment-scheduling', 'multi-language', 'ecommerce', 'premium-templates', 'white-label', 'audit-logs', 'feedback-system'];
  const isVipModule = vipModules.includes(json.moduleId);

  if (isVipModule && !isVip) {
    out.push({
      json: {
        success: false,
        error: 'M√≥dulo VIP requer plano VIP',
        moduleId: json.moduleId,
        currentPlan: plan,
        timestamp: new Date().toISOString()
      }
    });
  } else {
    out.push({
      json: {
        success: true,
        siteSlug: client.siteSlug,
        moduleId: json.moduleId,
        enabled: json.enabled,
        canToggle: true,
        timestamp: new Date().toISOString()
      }
    });
  }
}
return out;

// Atualizar m√≥dulos nas features
const out = [];
for (const { json } of items) {
  const fields = json.fields || {};
  const features = JSON.parse(fields.features || '{}');
  features[json.moduleId] = json.enabled;

  out.push({
    json: {
      recordId: json.id,
      siteSlug: json.siteSlug,
      features: JSON.stringify(features),
      timestamp: new Date().toISOString()
    }
  });
}
return out;
